sys_variable_value.value:
var userGR = new GlideRecord('sys_user');
userGR.get('sys_id', current.u_ess_request_for);

var gr = new GlideRecord('x_aaro2_rdc_cred_workstations');
gr.addQuery('request', current.sys_id);
gr.addQuery('status', '1');
gr.orderBy('order');
gr.query();
while(gr.next()){
	var res = updateWorkstation(gr.name, userGR.u_pos_user_id);
	var resObj = JSON.parse(res);

	if (resObj.Response == 'Success'){
		gr.status = 3;
	} else {
		gr.status = 2;
	}
	gr.update();
}

function updateWorkstation(workstation, userid){
	var rest, response, responseBody, responseError, responseStatus, endpointparms;

	endpointparms =  workstation + "/" + userid + "/" + userid;	
	
	try {
		rest = new sn_ws.RESTMessageV2();
		rest.setHttpMethod('GET');
		rest.setMIDServer(gs.getProperty('mid.server.rba_default'));
		rest.setEndpoint(gs.getProperty('x_aaro2_rdc_cred.update_workstation_endpoint') + endpointparms);
		rest.setRequestHeader('Content-Type', 'application/json');
		rest.setRequestHeader('Authorization', 'Basic ' + gs.getProperty('x_aaro2_rdc_cred.webservice_credential'));
		rest.setHttpTimeout(30000);
		response = rest.execute();
		response.waitForResponse(120);
		responseBody = response.getBody();
		responseError = response.haveError() ? response.getErrorMessage() : response.getBody();		
		responseStatus = response.getStatusCode();
	
		if (responseStatus != 200){
			responseBody = '';
			throw responseError;
		}
	}catch(ex){
		responseError = ex;
		gs.error('Remote Desktop Update Workstations', ex);
	}finally{
		return (responseBody) ? responseBody:'{"error":"' + responseError + '"}';		
	}
}

// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.
//
// For example,
//
answer = ifScript();

function ifScript() {
	var gr = new GlideRecord('x_aaro2_rdc_cred_workstations');
	gr.addQuery('request', current.sys_id);
	gr.addQuery('status', '2');
	gr.orderBy('order');
	gr.query();
	if (gr.hasNext()) {
		return 'yes';
	}
	return 'no';
}

if (workflow.scratchpad.workstations.length == 0) { //no workstations found at any store
	activity.result = 'all';
} else if (workflow.scratchpad.failedStores.length > 0) { //at least one store failed
	activity.result = 'some';
} else {
	activity.result = 'none';
}

workflow.scratchpad.workstations = [];
workflow.scratchpad.failedStores = []; 
workflow.scratchpad.failedStoresId = [];
var locationArray = current.locations.split(','); //array of sys_ids of stores
var order = 0;

//go through every store in the request (locationArray)
for(var i = 0; i < locationArray.length; i++){
	//gliderecord for getting name of store
	var gr = new GlideRecord('core_company');
	gr.addQuery('sys_id', locationArray[i]);
	gr.query();
	gr.next();
	
	var possibleWorkstations = getWorkstations(gr.name);
	
	if(possibleWorkstations.getStatusCode() == '200'){
		var workstations = possibleWorkstations.getBody().split(','); //make array of workstations
		for(var j = 0; j < workstations.length; j++){      //go through each workstation for the store
			workflow.scratchpad.workstations.push(workstations[j]); //put the workstation in scratchpad's array
			var workstationGR = new GlideRecord('x_aaro2_rdc_cred_workstations'); //create a new record for the workstations table
			workstationGR.initialize();
			workstationGR.request = current.sys_id;
			workstationGR.name = workstations[j].toString();
			workstationGR.status = '1'; //1=pending, 2=failed, 3=complete
			workstationGR.order = order;
			workstationGR.insert();
			order++;
		}
	}else{
		workflow.scratchpad.failedStoresId.push(gr.sys_id.toString());
		workflow.scratchpad.failedStores.push(gr.name.toString());
	}
}
current.failed_locations = workflow.scratchpad.failedStoresId.toString();

//get list of workstations for location (i.e. store number, 'C0006')
function getWorkstations(location){
	var workstationRestMessage, workstationResponse, workstationResponseBody, workstationStatus, workstationRequestBody, workstationResponseObj;
	try {
		workstationRestMessage = new sn_ws.RESTMessageV2();
		workstationRestMessage.setHttpMethod('get');
		workstationRestMessage.setMIDServer(gs.getProperty('mid.server.rba_default'));
		workstationRestMessage.setEndpoint(gs.getProperty('x_aaro2_rdc_cred.get_workstations_endpoint') + location);
		workstationRestMessage.setRequestHeader('Content-Type', 'application/json');
		workstationRestMessage.setRequestHeader('Authorization', 'Basic ' + gs.getProperty('x_aaro2_rdc_cred.webservice_credential'));
		workstationRestMessage.setHttpTimeout(6000);
		workstationResponse = workstationRestMessage.execute();
		workstationResponse.waitForResponse(120);
		workstationResponseBody = workstationResponse;
		workstationStatus = workstationResponse.getStatusCode();
	}catch(ex){
		var errorMessage = ex.getMessage();
		gs.error(errorMessage, 'Remote Desktop Get Workstations');
	}finally{
		workstationRequestBody = workstationRestMessage ? workstationRestMessage.getRequestBody() : null;
	}
	return workstationResponseBody;
}

